This is code sugar and is equivalent to 


[Test]
public void CheckParameters() 
{
	Mock mock = Mock<TestedClass>.MockNextInstance();
	mock.ExpectCall("SomeMethod").Args(Check<int>.AreSameType());

	TestedClass test = new TestedClass();
	test.SomeMethod(5);
	MockManager.Verify();
}


<Test()> _
Public Sub CheckParameters()
	Dim mock As Mock  = Mock(Of TestedClass).MockNextInstance()
	mock.ExpectCall("SomeMethod").Args(Check(Of Integer)).AreSameType())

	Dim test As TestedClass = New TestedClass
	test.SomeMethod(5)
	MockManager.Verify()
End Sub

