@helper traverse(IPublishedContent node)
{
 var cc = node.Children.Where(x=>x.IsVisible() && x.TemplateId > 0);                        
 if (cc.Count()>0)
 {
  <ul>
  @foreach (var c in cc)
  {
  <li>
   <a href="@c.NiceUrl()">@c.Name</a>
   @traverse(c)
  </li>
  }
  </ul>
 }
}

