function goToUser(user){$location.path(pathToUser(user)).search('orderBy',vm.usersOptions.orderBy).search('orderDirection',vm.usersOptions.orderDirection).search('pageNumber',vm.usersOptions.pageNumber).search('userStates',getUsersOptionsFilterCollectionAsDelimitedStringOrNull(vm.usersOptions.userStates)).search('userGroups',getUsersOptionsFilterCollectionAsDelimitedStringOrNull(vm.usersOptions.userGroups)).search('create',null).search('invite',null);}
function getUsersOptionsFilterCollectionAsDelimitedStringOrNull(collection){if(collection&&collection.length>0){return collection.join(',');}
return null;}
function getEditPath(user){return pathToUser(user)+usersOptionsAsQueryString();}
function pathToUser(user){return'/users/users/user/'+user.id;}
function usersOptionsAsQueryString(){var qs='?orderBy='+vm.usersOptions.orderBy+'&orderDirection='+vm.usersOptions.orderDirection+'&pageNumber='+vm.usersOptions.pageNumber+'&filter='+vm.usersOptions.filter;qs+=addUsersOptionsFilterCollectionToQueryString('userStates',vm.usersOptions.userStates);qs+=addUsersOptionsFilterCollectionToQueryString('userGroups',vm.usersOptions.userGroups);qs+='&mculture='+$location.search().mculture;return qs;}
function addUsersOptionsFilterCollectionToQueryString(name,collection){if(collection&&collection.length>0){return'&'+name+'='+collection.join(',');}
return'';}
function getUsers(){vm.loading=true;usersResource.getPagedResults(vm.usersOptions).then(function(data){vm.users=data.items;vm.usersOptions.pageNumber=data.pageNumber;vm.usersOptions.pageSize=data.pageSize;vm.usersOptions.totalItems=data.totalItems;vm.usersOptions.totalPages=data.totalPages;formatDates(vm.users);setUserDisplayState(vm.users);vm.userStatesFilter=usersHelper.getUserStatesFilter(data.userStates);initUserStateSelections();vm.loading=false;},function(error){vm.loading=false;});}
function setUserDisplayState(users){angular.forEach(users,function(user){user.userDisplayState=usersHelper.getUserStateFromValue(user.userState);});}
function formatDates(users){angular.forEach(users,function(user){if(user.lastLoginDate){var dateVal;var serverOffset=Umbraco.Sys.ServerVariables.application.serverTimeOffset;var localOffset=new Date().getTimezoneOffset();var serverTimeNeedsOffsetting=-serverOffset!==localOffset;if(serverTimeNeedsOffsetting){dateVal=dateHelper.convertToLocalMomentTime(user.lastLoginDate,serverOffset);}else{dateVal=moment(user.lastLoginDate,'YYYY-MM-DD HH:mm:ss');}
userService.getCurrentUser().then(function(currentUser){user.formattedLastLogin=dateVal.locale(currentUser.locale).format('LLL');});}});}
function setBulkActions(users){vm.allowDisableUser=true;vm.allowEnableUser=true;vm.allowUnlockUser=true;vm.allowSetUserGroup=true;var firstSelectedUserGroups;angular.forEach(users,function(user){if(!user.selected){return;}
if(user.isCurrentUser){vm.allowDisableUser=false;vm.allowEnableUser=false;vm.allowUnlockUser=false;vm.allowSetUserGroup=false;return false;}
if(user.userDisplayState&&user.userDisplayState.key==='Disabled'){vm.allowDisableUser=false;}
if(user.userDisplayState&&user.userDisplayState.key==='Active'){vm.allowEnableUser=false;}
if(user.userDisplayState&&user.userDisplayState.key==='Invited'){vm.allowEnableUser=false;}
if(user.userDisplayState&&user.userDisplayState.key==='LockedOut'){vm.allowEnableUser=false;}
if(user.userDisplayState&&user.userDisplayState.key!=='LockedOut'){vm.allowUnlockUser=false;}
if(vm.allowSetUserGroup===true){if(!firstSelectedUserGroups){firstSelectedUserGroups=user.userGroups.map(function(ug){return ug.alias;});}else{var userGroups=user.userGroups.map(function(ug){return ug.alias;});if(_.difference(firstSelectedUserGroups,userGroups).length>0){vm.allowSetUserGroup=false;}}}});}
